// Login.jsx
import React, { useContext, useState } from 'react';
import './CSS/LoginSignup.css';
import { Link } from 'react-router-dom';
import { ShopContext } from '../Context/ShopContext';
import { userLogin } from '../Components/API';

const Login = () => {
  const { login } = useContext(ShopContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleLogin = async (event) => {
    event.preventDefault();

    try {
      const response = await userLogin({ username, password });
      // Call the login function from the context to update the user state
      login({ username: response.username }); 
    } catch (error) {
      console.error('Error during login:', error);
      setError('Invalid username or password. Please try again.');
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <div className="loginsignup-fields">
            <input type="text" name="username" placeholder='Username' onChange={(e) => setUsername(e.target.value)} required />
            <input type="password" name="password" placeholder='Password' onChange={(e) => setPassword(e.target.value)} required />
          </div>
          {error && <p className="login-error">{error}</p>}
          <button type="submit">Login</button>
        </form>
        <p className="loginsignup-login">
          Don't have an account? <Link to='/signup'>Sign up here</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
