import React, { createContext, useState, useEffect } from "react";
import all_product from "../Components/Assets/all_product";
import { getAllProducts } from "../Components/API";

const getDefaultCart = () => {
  let cart = {};
  for (let index = 0; index < all_product.length; index++) {
    cart[all_product[index].id] = 0;
  }
  return cart;
};

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [cartItems, setCartItems] = useState(getDefaultCart());
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await getAllProducts();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    }

    fetchData();
  }, []);

  const login = () => {
    setLoggedIn(true);
  };

  const logout = () => {
    setLoggedIn(false);
    console.log('User logged out');
  };

  const addToCart = (productId) => {
    setCartItems((prev) => ({ ...prev, [productId]: (prev[productId] || 0) + 1 }));
    console.log(cartItems);
  };

  const removeFromCart = (productId) => {
    setCartItems((prev) => ({ ...prev, [productId]: Math.max(0, prev[productId] - 1) }));
  };

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const productId in cartItems) {
      if (cartItems[productId] > 0) {
        let itemInfo = all_product.find((product) => product.id === Number(productId));
        totalAmount += cartItems[productId] * itemInfo.new_price;
      }
    }
    return totalAmount;
  };

  const getTotalCartItems = () => {
    let totalItem = 0;
    for (const productId in cartItems) {
      if (cartItems[productId] > 0) {
        totalItem += cartItems[productId];
      }
    }
    return totalItem;
  };

  const contextValue = {
    getTotalCartItems,
    getTotalCartAmount,
    all_product,
    cartItems,
    products, // Include the fetched products in the context
    addToCart,
    removeFromCart,
    login,
    logout,
    loggedIn,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
