// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { userLogin, getUser } from '../../API';

export default function Login({ setToken, setId, setUsernameProp }) {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();

    const userObj = {
      username,
      password,
    };

    try {
      const response = await userLogin(userObj);
      const { token } = response;
      const users = await getUser();
      const user = users.find(_user => _user.username === username);

      localStorage.setItem("token", token);
      setToken(token);
      setId(user.id);
      setUsernameProp(user.username);

      navigate("/home");

      console.log(`Logged in with userId: ${user.id}`);
    } catch (err) {
      console.error('Error during login:', err);
      setError('Invalid username or password. Please try again.');
    }
  }

  return (
    <div className='page-container'>
      <div className='user-info-form'>
        <h2>Login Now!</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Username: <input value={username} onChange={(e) => setUsername(e.target.value)} />
          </label>
          <label>
            Password: <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </label>
          {error && <p className="login-error">{error}</p>}
          <button className='submit-button'>Login</button>
        </form>
      </div>
    </div>
  );
}
