// Login.jsx
import React, { useContext, useState } from 'react';
import './CSS/LoginSignup.css';
import { Link } from 'react-router-dom';
// import { ShopContext } from '../Context/ShopContext';

const Login = () => {
  const { login, setToken } = useContext(ShopContext);
  const [error, setError] = useState(null);

  const handleLogin = async (event) => {
    event.preventDefault();

    const username = event.target.elements.username.value;
    const password = event.target.elements.password.value;

    try {
      // Fetch all users
      const usersResponse = await fetch('https://fakestoreapi.com/users');
      const users = await usersResponse.json();

      // Check if the entered username and password match any user
      const user = users.find(u => u.username === username && u.password === password);

      if (user) {
        // Call the login function from the context to update the user state
        login({ username: user.username });
        // Set token if setToken function is available
        setToken && setToken('your-token'); // replace 'your-token' with the actual token
      } else {
        setError('Invalid username or password. Please try again.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <div className="loginsignup-fields">
            <input type="text" name="username" placeholder='Username' required />
            <input type="password" name="password" placeholder='Password' required />
          </div>
          {error && <p className="login-error">{error}</p>}
          <button type="submit">Login</button>
        </form>
        <p className="loginsignup-login">
          Don't have an account? <Link to='/signup'>Sign up here</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
