// LoginSignup.jsx
import React from 'react';
import './CSS/LoginSignup.css';
import { Link } from 'react-router-dom';
import { createAccount } from '../API';

const LoginSignup = () => {
  const handleSignup = async (event) => {
    event.preventDefault();
    
    const name = event.target.elements.name.value;
    const email = event.target.elements.email.value;
    const password = event.target.elements.password.value;

    try {
      // Replace this with your actual API call for user registration
      const token = await createAccount({ name, email, password });
      console.log('User signed up with token:', token);
      // Redirect or perform any other action after successful signup
    } catch (error) {
      console.error('Error during signup:', error);
      // Handle signup error (e.g., display an error message to the user)
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Sign Up</h1>
        <form onSubmit={handleSignup}>
          <div className="loginsignup-fields">
            <input type="text" name="name" placeholder='Your Name' required />
            <input type="email" name="email" placeholder='Email Address' required />
            <input type="password" name="password" placeholder='Password' required />
          </div>
          <button type="submit">Continue</button>
        </form>
        <p className="loginsignup-login">
          Already have an account? <Link to='/login'>Login here</Link>
        </p>
        <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>By continuing, I agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  );
}

export default LoginSignup;
