import React, { useContext } from "react";
import { ShopContext } from "../Context/ShopContext";

const CartItems = () => {
  const { cartItems, removeFromCart, all_product } = useContext(ShopContext);

  return (
    <div className="cartitems-format-main">
      <p>Products</p>
      <p>Title</p>
      <p>Price</p>
      <p>Total</p>
      <p>Remove</p>
      {Object.keys(cartItems).map((productId) => {
        const quantity = cartItems[productId];
        if (quantity > 0) {
          const product = all_product.find((item) => item.id === parseInt(productId));
          const { title, new_price } = product;

          return (
            <div key={productId} className="cartitems-format">
              <div className="cartitems-product">
                <img src={product.image} alt={title} />
              </div>
              <p>{title}</p>
              <p>${new_price.toFixed(2)}</p>
              <p>${(quantity * new_price).toFixed(2)}</p>
              <button onClick={() => removeFromCart(productId)}>Remove</button>
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default CartItems;
