// CartItems.jsx

import React, { useContext, useEffect, useState } from 'react';
import './CartItems.css';
import remove_icon from '../Assets/cart_cross_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import { Link } from 'react-router-dom';

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);
  const [itemsData, setItemsData] = useState([]);

  useEffect(() => {
    const fetchItemsData = async () => {
      const itemsData = await Promise.all(
        Object.entries(cartItems).map(async ([itemId, quantity]) => {
          const item = all_product.find((product) => product.id === Number(itemId));
          return { item, quantity };
        })
      );
      setItemsData(itemsData);
    };

    fetchItemsData();
  }, [all_product, cartItems]);

  return (
    <div className='cartitems'>
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {itemsData.map(({ item, quantity }, index) => {
        console.log("Item:", item); // Add this line for debugging
        console.log("Item Name:", item ? item.name : "N/A"); // Add this line for debugging

        if (quantity > 0 && item) {
          return (
            <div key={index}>
              <div className="cartitems-format cartitems-format-main">
                <img src={item.image} alt="" className='carticon-product-icon' />
                <p>{item.name}</p>
                <p>${item.new_price}</p>
                <button className='cartitems-quantity'>{quantity}</button>
                <p>${item.new_price * quantity}</p>
                <img className='cartitems-remove-icon' src={remove_icon} onClick={() => { removeFromCart(item.id) }} alt="" />
              </div>
              <hr />
            </div>
          );
        }
        return null;
      })}
      <div className="cartitems-down">
        {/* ... rest of the code ... */}
      </div>
    </div>
  );
}

export default CartItems;
