import React, { useEffect, useState } from 'react'
import './ProductDisplay.css'
import { BsPlus, BsEyeFill } from 'react-icons/bs';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';


const ProductDisplay = (props) => {

    const {title, price, id, image, category, description} = props;
    const location = useLocation();
    const productDisplayItem = location.pathname.includes(`/home/${id}`);
    const [ cart, setCart] = useState([]);

    const productDisplay = {
      width: productDisplayItem ? '600px' : '300px',
      height: productDisplayItem ? '700px' : '400px',
    };

    const addToCart = (props) => {

      const itemDisplay = JSON.parse(localStorage.getItem('itemDisplay')) || [];

      const result = itemDisplay.find(item=> item.id === props.id)
      if(!result) {
        const updatedDisplay = [...itemDisplay,{...props, quantity:1}];
        setCart(updatedDisplay);
        localStorage.setItem('itemDisplay', JSON.stringify(updatedDisplay));
      } else {
        result.quantity += 1
        const otherItemsInCart = itemDisplay.filter(item => item.id != result.id);
        const updatedDisplay = [...otherItemsInCart, result];
        setCart(updatedDisplay);
        localStorage.setItem('itemDisplay', JSON.stringify(updatedDisplay));
      };
    };

  return (
    <div className="product-card-container" style={cardStyle}>
            <img src={image} className="product-image" alt={`product name: ${title}`} />
            {productDisplayItem && <p>{description}</p>}
            {!productDisplayItem && (
            <div className="buttons">
                <button className="add-to-cart" onClick = {() => addToCart(props)}>
                    <BsPlus/>
                </button>
                <Link to={`/home/${id}`} className='product-details'>
                    <BsEyeFill/>
                </Link>
            </div>
            )}
            <h3>{category}</h3>
            <Link to={`/home/${id}`}>
                <h2>{title}</h2>
            </Link>
            <h3>${price}</h3>
        </div>
  );
};

export default ProductDisplay
