// ShopContext.jsx
import React, { createContext, useState, useEffect } from "react";
import all_product from "../Components/Assets/all_product";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let index = 0; index < all_product.length + 1; index++) {
    cart[index] = 0;
  }
  return cart;
};

const ShopContextProvider = (props) => {
  const [cartItems, setCartItems] = useState(getDefaultCart());
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if the user is logged in (using token in local storage)
    const storedToken = localStorage.getItem("authToken");
    if (storedToken) {
      // Fetch user details using the token (replace with actual API call)
      // For simplicity, let's assume the API returns user details with the token
      const userFromToken = { id: 1, username: "exampleUser" };
      setUser(userFromToken);
    }
  }, []);

  const login = (token) => {
    // Save the token in local storage
    localStorage.setItem("authToken", token);
    // Fetch user details using the token (replace with actual API call)
    // For simplicity, let's assume the API returns user details with the token
    const userFromToken = { id: 1, username: "exampleUser" };
    setUser(userFromToken);
  };

  const logout = () => {
    // Remove the token from local storage
    localStorage.removeItem("authToken");
    setUser(null);
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    console.log(cartItems);
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = all_product.find((product) => product.id === Number(item));
        totalAmount += cartItems[item] * itemInfo.new_price;
      }
    }
    return totalAmount;
  };

  const getTotalCartItems = () => {
    let totalItem = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        totalItem += cartItems[item];
      }
    }
    return totalItem;
  };

  // ... rest of the functions

  const contextValue = {
    getTotalCartItems,
    getTotalCartAmount,
    all_product,
    cartItems,
    addToCart,
    removeFromCart,
    user,
    login,
    logout,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
